package com.uchain.storage;import com.google.common.collect.Maps;import lombok.Getter;import lombok.Setter;import java.util.Map;@Getter@Setterpublic class Batch {    private Map<byte[],byte[]> ops = Maps.newHashMap();    public Batch put(byte[] key,byte[] value){        ops.put(key,value);        return this;    }    public Batch delete(byte[] key){        ops.remove(key);        return this;    }}