package com.uchain;import java.util.Comparator;import java.util.Iterator;import java.util.Map;import java.util.TreeMap;public class SortDemo2 {        private final static int SIZE = 5;    public static void main(String[] args) {        System.out.println("---------------- key 为 Sting 排序结果-----------------");        String s = new String();        createTreeMap(s);        System.out.println("---------------- key 为 Integer 排序结果-----------------");        Integer l = new Integer(0);        createTreeMap(l);    }        public static void createTreeMap(Object obj) {                TreeMap<Object, Object> map = new TreeMap<>(new Comparator<Object>() {            @Override            public int compare(Object o1, Object o2) {                if(o1 instanceof String && o2 instanceof String) {                    return ((String) o1).compareTo((String) o2);                } else if(o1 instanceof Integer && o2 instanceof Integer) {                    return ((Integer) o1).compareTo((Integer) o2);                }                return 0;            }                    });                for(int i = 1; i<SIZE; i++) {            if(obj instanceof String) {                map.put(String.valueOf(i), String.valueOf(i));            }            if(obj instanceof Integer) {                map.put(Integer.valueOf(i), Integer.valueOf(i));            }        }                print(map);    }        public static void print(Map<Object, Object> map) {        Iterator<Map.Entry<Object, Object>> it = map.entrySet().iterator();        while(it.hasNext()) {            Map.Entry<Object, Object> entry = it.next();            System.out.println(entry.getKey() + " : " + entry.getValue());        }    }}